import {
  fetchNewsPending,
  fetchNewsSuccess,
  fetchNewsError
} from '../actions';

function fetchNews() {
  return   (dispatch) => {
    dispatch(fetchNewsPending());
      fetch('http://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=759652119d0a41f8acc1c5b07ad5263a')
      .then(res => res.json())
      .then(res => {
        dispatch(fetchNewsSuccess(res.articles))
        console.log(res.articles)
        return res;
      })
      .catch(error => {
        dispatch(fetchNewsError(error));
      })
  }
}

export default fetchNews;
