import React, { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import fetchProductsAction from '../../reducers/fetchProduct';
import LoadingSpinner from '../spinner';
import { compose } from "../../utils";
import { withBookstoreService } from "../hoc";
import ErrorBoundry from "../error-boundry";

class ProductView extends Component {
  constructor(props) {
    super(props);


  }

  componentDidMount() {

    const { fetchProducts, pending, error } = this.props;

     fetchProducts();
  //  console.log(fetchProducts)
   // console.log(this.props.fetchProducts())
  }



  

  render() {
    const { fetchProducts, pending, error } = this.props;
    if (pending){
     return (
       <LoadingSpinner/>
       )
    }if (error){
      return (
        <ErrorBoundry/>
      )
    }else
    
    return (
      <div className='product-list-wrapper'>
        {/*<button onClick={this.props.fetchProducts}>Click here</button>*/}
        {/*<h1>Welcome here</h1>*/}
        {/*<h1>{products}</h1>*/}
        {/*{error && <span className='product-list-error'>{error}</span>}*/}
        {/*/!*<ProductList products={products} />*!/*/}
      </div>
    )
  }
}


const mapStateToProps = ({ newsList: { products, pending, error}}) => {
  return { products, pending, error };
};



const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchProducts: fetchProductsAction
  
}, dispatch)


export default compose(
  withBookstoreService(),
  connect(mapStateToProps, mapDispatchToProps)
)(ProductView);
