const initialState = {
  pending: false,
  products: [
    { author: 'Diana', content: 'love',  description: 'game', publishedAt: 'today', title: 'dian', url:''},
    { author: 'danik', content: 'hi',  description: 'game', publishedAt: 'today', title: 'danik', url:''}
  ],
  error: null
}

export default function newsList(state = initialState, action) {
  switch(action.type) {
    case 'FETCH_NEWS_PENDING':
      return {
          pending: true,
          products: state.products,
          error: null
        }
    case 'FETCH_NEWS_SUCCESS':
      return {
          pending: false,
          products: action.payload,
          error: null
        }
    case 'FETCH_NEWS_ERROR':
      return {
          pending: false,
          products: [],
          error: action.error
      }
    default:
      return state;
      
  }
}
