import {
  fetchNewsPending,
  fetchNewsSuccess,
  fetchNewsError
} from '../actions';

function fetchNews() {
  return async dispatch => {
    dispatch(fetchNewsPending());
    await fetch('http://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=759652119d0a41f8acc1c5b07ad5263a')
      .then(res => dispatch(fetchNewsSuccess(res.articles)))
      .catch(error => {
        dispatch(fetchNewsError(error));
      })
  }
}

export default fetchNews;
