const initialState = {
  pending: false,
  products: [
    { author: 'Diana', content: 'love',  description: 'game', publishedAt: 'today', title: 'dian', url:''},
    { author: 'danik', content: 'hi',  description: 'game', publishedAt: 'today', title: 'danik', url:''}
  ],
  error: null,
  arr: 'array'
}

export default function newsList(state = initialState, action) {
  switch(action.type) {
    case 'FETCH_NEWS_PENDING':
      return {
          pending: true,
          products: action.products,
          error: null,
          arr: 'pending'
        }
    case 'FETCH_NEWS_SUCCESS':
      return {
          pending: false,
          products: action.products,
          error: null,
          arr: 'success'
        }
    case 'FETCH_NEWS_ERROR':
      return {
          pending: false,
          products: [],
          error: action.error
      }
    default:
      return state;
      
  }
}



export const getProducts = state => state.products;
export const getProductsPending = state => state.pending;
export const getProductsError = state => state.error;
