import React from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import fetchProductsAction from '../../reducers/fetchProduct';

import {
  getProductsError,
  getProducts,
  getProductsPending
} from '../../reducers/newsList';

import {compose} from "../../utils";
import {withBookstoreService} from "../hoc";

const News = ({ products, fetchProducts, error, pending }) => {
  console.log(products.map((news)=> {return news.author}))
  console.log(error)
  console.log(pending)
  // const news = products.map((news, index)=> {
  //   return (
  //     <li key={index}>
  //    <h1> {news.content} </h1>
  //     </li>)
  // })


  return (
    <div className='product-list-wrapper'>
      <button onClick={fetchProducts}>Click here</button>
      <h1>sdsdsdsds</h1>
      {/*<ul>{ news }</ul>*/}
    </div>
  )
}


const mapStateToProps = state => ({
  error: getProductsError(state.newsList),
  products: getProducts(state.newsList),
  pending: getProductsPending(state.newsList)
})


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchProducts: fetchProductsAction
  
}, dispatch)


export default compose(
  withBookstoreService(),
  connect(mapStateToProps, mapDispatchToProps)
)(News);
