export default function newsList(state , action) {
  switch(action.type) {
    case 'FETCH_NEWS_PENDING':
      return {
          pending: true,
          products: state.products,
          error: null
        }
    case 'FETCH_NEWS_SUCCESS':
      return {
          pending: false,
          products: action.payload,
          error: null
        }
    case 'FETCH_NEWS_ERROR':
      return {
          pending: false,
          products: state.products,
          error: action.error
      }
    default:
      return State;
      
  }
}
