import {
  fetchNewsPending,
  fetchNewsSuccess,
  fetchNewsError
} from '../actions';

function fetchNews() {
  return   (dispatch) => {
    dispatch(fetchNewsPending());
      fetch('http://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=759652119d0a41f8acc1c5b07ad5263a')
      .then(res => res.json())
      .then(payload => {
        dispatch({type: 'FETCH_NEWS_SUCCESS',
          payload})
        console.log(payload.articles)
        return payload;
      })
      .catch(error => {
        dispatch(fetchNewsError(error));
      })
  }
}

export default fetchNews;
