import React from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {fetchNews} from '../../actions';

import {compose} from "../../utils";
import {withBookstoreService} from "../hoc";
import LoadingSpinner from "../spinner";
import ErrorBoundry from "../error-boundry";
import '../news/news.css'

const User = ({products, fetchProducts, error, pending}) => {

  
  const news = products.map((news, index) => {
    return (
      <li key={index}>
        <h3> { news.author } </h3>
        { news.author }
        { news.title }
        { news.description }
        { news.url }
        { news.urlToImage }
        { news.publishedAt }
        { news.content }
        { news.source.id }
        { news.source.name }
      </li>
    )
  })
  if (pending) {
    return (
      <LoadingSpinner/>
    )
  }
  if (error) {
    return (
      <ErrorBoundry/>
    )
  } else
    return (
      <div className='product-list-wrapper'>
        <ul>{ news }</ul>
      </div>
    )
}


const mapStateToProps = ({newsList: {products, pending, error}}) => {
  return {products, pending, error};
};


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchProducts: fetchNews
  
}, dispatch)


export default compose(withBookstoreService(), connect(mapStateToProps, mapDispatchToProps))(User);
