const booksRequested = () => {
  return {
    type: 'FETCH_BOOKS_REQUEST'
  };
};

const booksLoaded = (newBooks) => {
  return {
    type: 'FETCH_BOOKS_SUCCESS',
    payload: newBooks
  };
};

const booksError = (error) => {
  return {
    type: 'FETCH_BOOKS_FAILURE',
    payload: error
  };
};

export const bookAddedToCart = (bookId) => {
  return {
    type: 'BOOK_ADDED_TO_CART',
    payload: bookId
  };
};

export const bookRemovedFromCart = (bookId) => {
  return {
    type: 'BOOK_REMOVED_FROM_CART',
    payload: bookId
  };
};

export const allBooksRemovedFromCart = (bookId) => {
  return {
    type: 'ALL_BOOKS_REMOVED_FROM_CART',
    payload: bookId
  };
};

export function fetchNewsPending() {
  return {
    type: 'FETCH_NEWS_REQUEST'
  }
}

export  function  fetchNewsSuccess(news) {
  return {
    type: 'FETCH_NEWS_SUCCESS',
    products: news
  }
}

export function fetchNewsError(error) {
  return {
    type: 'FETCH_NEWS_ERROR',
    products: error
  }
}

 export const fetchBooks = (bookstoreService) => () => (dispatch) => {
  dispatch(booksRequested());
  bookstoreService.getBooks()
    .then((data) => dispatch(booksLoaded(data)))
    .catch((err) => dispatch(booksError(err)));
};

export function fetchNews() {
  return async dispatch => {
    dispatch(fetchNewsPending());
    await fetch('http://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=759652119d0a41f8acc1c5b07ad5263a')
      .then(res => res.json())
      .then(res => {
        if (res.error) {
          throw(res.error);
        }
         // console.log(res.articles)
        dispatch(fetchNewsSuccess(res.articles))
        return res;
      })
      .catch(error => {
        dispatch(fetchNewsError(error));
      })
  }
}
