import React, { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
//import {fetchNewsSuccess} from '../../actions/index'

import fetchProductsAction from '../../reducers/fetchProduct';
import { getProductsError, getProducts, getProductsPending } from '../../reducers/newsList';

//import LoadingSpinner from '../spinner';
import { compose } from "../../utils";
import { withBookstoreService } from "../hoc";

class ProductView extends Component {
  // constructor(props) {
  //   super(props);
  //
  //   // this.shouldComponentRender = this.shouldComponentRender.bind(this);
  // }

  // componentDidUpdate(prevProps, prevState, snapshot) {
  //
  //   const { fetchProducts } = this.props;
  //
  //   fetchProducts();
  // //  console.log(fetchProducts)
  //   console.log(this.props.fetchProducts())
  // }
  //
  // shouldComponentRender() {
  //   const { pending } = this.props;
  //   if(this.pending === false) return false;
  //   // more tests
  //   return true;
  // }
  //
  

  render() {
  const {products, error, pending} = this.props;
    // console.log(this.props.getProducts)
    // console.log(products)
    // console.log(error)
    // console.log(pending)
    
    // if(!this.shouldComponentRender()) return <LoadingSpinner />
    
    return (
      <div className='product-list-wrapper'>
        <button onClick={this.props.fetchProducts}>Click here</button>
        <h1>Welcome here</h1>
        {/*<h1>{this.props.products}</h1>*/}
        {error && <span className='product-list-error'>{error}</span>}
        {/*<ProductList products={products} />*/}
      </div>
    )
  }
}


const mapStateToProps = state => ({
  error: getProductsError(state),
  products: getProducts(state),
  pending: getProductsPending(state)
})


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchProducts: fetchProductsAction
  
}, dispatch)


export default compose(
  withBookstoreService(),
  connect(mapStateToProps, mapDispatchToProps)
)(ProductView);
