import React, {Component} from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {fetchNews} from '../../actions/index';
import LoadingSpinner from '../spinner';
import {compose} from "../../utils";
import {withBookstoreService} from "../hoc";
import ErrorBoundry from "../error-boundry";

class ProductView extends Component {
  
  componentDidMount() {
    // const {fetchProducts} = this.props;
    this.props.fetchProducts();
  }
  
  render() {
    
    const { pending, error, products, fetchProducts } = this.props;
   // console.log(products)
    if (pending) {
      return (
        <LoadingSpinner/>
      )
    }if (error) {
      return (
        <ErrorBoundry/>
      )
    }else {
      return (
        <div className='product-list-wrapper'>
          <News products={products}/>
          <button onClick={fetchProducts}>Click here</button>
        </div>
      )
    }
  }
}

const News = ({products}) => {
  const news = products
    .map((news, index) =>
    <li key={index}>
      <image href={news.urlToImage} src={news.urlToImage}/>
      { news.author }
      { news.title }
      { news.description }
      { news.url }
      { news.urlToImage }
      { news.publishedAt }
      { news.content }
      <div className="jumbotron">
        <h3 className="display-8">{news.title}</h3>
        <p>{ news.description }</p>
        <hr className="my-4"/>
          <p>It uses utility classes for typography and spacing to space content
            out within the larger container.</p>
          <p className="lead">
            <a className="btn btn-primary btn-lg" href="#" role="button">Learn
              more</a>
          </p>
      </div>
      
    </li>
  );
  return (
    <ul>{ news }</ul>
  )
}


const mapStateToProps = ({newsList: {products, pending, error}}) => {
  return {products, pending, error};
};


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchProducts: fetchNews
  
}, dispatch)


export default compose(
  withBookstoreService(),
  connect(mapStateToProps, mapDispatchToProps)
)(ProductView);
