import React from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {
  fetchNews,
  fetchNewsError,
  fetchNewsPending,
  fetchNewsSuccess
} from '../../actions';

import {compose} from "../../utils";
import {withBookstoreService} from "../hoc";
// import LoadingSpinner from "../spinner";
// import ErrorBoundry from "../error-boundry";
import '../news/news.css'

class User extends React.Component {
  
  _apiBase = "http://localhost:5000/auth";
  
  getResource = async (url) => {
    const res = await fetch(`${this._apiBase}${url}`, {
      mode: "no-cors"
    });
    
    if (!res.ok) {
      throw new Error(`CCould not fetch ${url}` +
        `, received ${res.status}`)
    }
    return  res;
    
  };
  getAllPeople = async () => {
    const res = await this.getResource(``);
    console.log(res)
    return await res.json()
  };
  
  add = () => {
    this.getAllPeople()
      .then((res)=> { console.log(res)})
      .catch(err => console.log(err))
  }
  render() {
    //console.log(this.getAllPeople())
    return (
      <div>
        <h1>Home</h1>
        <button onClick={() => this.add()}>click</button>
        {/*<button onClick={() => this.fetchN()}>Cclick</button>*/}
      </div>
    
    )
  }
}



const mapStateToProps = ({newsList: {products, pending, error}}) => {
  return {products, pending, error};
};


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchProducts: fetchNews
  
}, dispatch)


export default compose(withBookstoreService(), connect(mapStateToProps, mapDispatchToProps))(User);
