import React from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {fetchUser} from '../../actions';
import axios from 'axios';

import {compose} from "../../utils";
import {withBookstoreService} from "../hoc";
// import LoadingSpinner from "../spinner";
// import ErrorBoundry from "../error-boundry";
import '../news/news.css'

class User extends React.Component {
  state = {
    email: 'danik@kkkkkkkk.ru',
    password: 'Danikk1234'
  }
  
  // getResource = async (url) => {
  //   const res = await fetch(`http://localhost:5000/auth/register${url}`, {
  //     method: 'POST',
  //     mode: "no-cors",
  //     headers: {
  //       'Content-Type': 'application/json'
  //     },
  //     body: JSON.stringify(this.state)
  //   });
  //
  //   if (!res.ok) {
  //     throw new Error(`CCCould not fetch ${url}` +
  //       `, received ${res.status}`)
  //   }
  //   return await res.json();
  // };
  //
  // getAllPeople = async () => {
  //   const res = await this.getResource(``);
  //   console.log(res)
  //   return res
  // };
  
  
  add = (e) => {
    e.preventDefault()
    axios.post('http://localhost:5000/auth/register', this.state)
      .then((res) => {
        console.log(res)
      })
      .catch((err) => console.log(err))
  }
  
  
  render() {
    console.log(this.props.products)
    return (
      <div>
        <h1>Home</h1>
        <button onClick={this.props.fetchUser}>click</button>
        
        
        <form>
          <div className="form-group">
            <label htmlFor="exampleInputEmail1">Email address</label>
            <input type="email" className="form-control" id="exampleInputEmail1"
                   aria-describedby="emailHelp"/>
            <small id="emailHelp" className="form-text text-muted">We'll never
              share your email with anyone else.</small>
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputPassword1">Password</label>
            <input type="password" className="form-control"
                   id="exampleInputPassword1"/>
          </div>
          <div className="form-group form-check">
            <input type="checkbox" className="form-check-input"
                   id="exampleCheck1"/>
            <label className="form-check-label" htmlFor="exampleCheck1">Check
              me out</label>
          </div>
          <button type="submit"
                  className="btn btn-primary"
                  onClick={this.add}
          >
            Submit
          </button>
        </form>
      </div>
    
    )
  }
}


const mapStateToProps = ({userList: {products, pending, error}}) => {
  return {products, pending, error};
};


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchUser: fetchUser
  
}, dispatch)


export default compose(withBookstoreService(), connect(mapStateToProps, mapDispatchToProps))(User);
